# Description: Firefly Algorithm: A metaheuristic algorithm inspired by the flashing behavior of fireflies to find optimal solutions by attracting each other towards brighter fireflies.
# Code:
# ```python
import numpy as np

class FireflyAlgorithm:
    def __init__(self, budget, dim, alpha=0.2, beta0=1.0, gamma=1.0):
        self.budget = budget
        self.dim = dim
        self.alpha = alpha
        self.beta0 = beta0
        self.gamma = gamma

    def attractiveness(self, x):
        return 1 / (1 + np.linalg.norm(x))

    def move_firefly(self, x, x_best):
        r = np.linalg.norm(x - x_best)
        beta = self.beta0 * np.exp(-self.gamma * r**2)
        return x + self.alpha * (np.random.rand(self.dim) - 0.5) + beta * (x_best - x)

    def __call__(self, func):
        population = np.random.uniform(-5.0, 5.0, (self.budget, self.dim))
        fitness = np.apply_along_axis(func, 1, population)
        best_idx = np.argmin(fitness)
        x_best = population[best_idx]

        for _ in range(self.budget):
            for i in range(self.budget):
                for j in range(self.budget):
                    if fitness[j] < fitness[i]:
                        population[i] = self.move_firefly(population[i], population[j])
                        fitness[i] = func(population[i])

            best_idx = np.argmin(fitness)
            x_best = population[best_idx]

        return x_best
# ```
