# Description: A novel metaheuristic algorithm called Harmony Search with Dynamic Pitch Adjustment for solving black box optimization problems efficiently.
# Code:
# ```python
import numpy as np

class HarmonySearch:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.lower_bound = -5.0
        self.upper_bound = 5.0

    def __call__(self, func):
        def generate_harmony():
            return np.random.uniform(self.lower_bound, self.upper_bound, self.dim)

        def pitch_adjustment(harmony):
            r = np.random.uniform(0, 1, self.dim)
            new_harmony = harmony + r * np.random.uniform(-1, 1, self.dim)
            return np.clip(new_harmony, self.lower_bound, self.upper_bound)

        harmony_memory = [generate_harmony() for _ in range(5)]
        for _ in range(self.budget):
            new_harmony = generate_harmony()
            if np.random.rand() < 0.5:
                new_harmony = pitch_adjustment(new_harmony)
            harmony_memory.sort(key=lambda x: func(x))
            if func(new_harmony) < func(harmony_memory[-1]):
                harmony_memory[-1] = new_harmony
        return harmony_memory[0]
# ```
