# Description: An algorithm that combines Differential Evolution (DE) with Particle Swarm Optimization (PSO) to achieve a balance between exploration and exploitation for black box optimization problems.
# Code:
# ```python
import numpy as np

class DEPSO:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.pop_size = 30
        self.max_gen = budget // self.pop_size
        self.w = 0.5
        self.c1 = 1.49445
        self.c2 = 1.49445
        self.CR = 0.7
        self.F = 0.5

    def __call__(self, func):
        def initialize_population():
            return np.random.uniform(-5.0, 5.0, (self.pop_size, self.dim))

        def boundary_check(p):
            return np.clip(p, -5.0, 5.0)

        def evaluate_population(population):
            return np.array([func(ind) for ind in population])

        def DEPSO_optimization():
            population = initialize_population()
            fitness = evaluate_population(population)
            global_best_idx = np.argmin(fitness)
            global_best = population[global_best_idx]

            for _ in range(self.max_gen):
                for i in range(self.pop_size):
                    r1, r2, r3 = np.random.choice(self.pop_size, 3, replace=False)
                    mutant = boundary_check(population[r1] + self.F * (population[r2] - population[r3]))
                    trial = np.where(np.random.rand(self.dim) < self.CR, mutant, population[i])
                    trial_fit = func(trial)

                    if trial_fit < fitness[i]:
                        population[i] = trial
                        fitness[i] = trial_fit

                    if trial_fit < fitness[global_best_idx]:
                        global_best_idx = i
                        global_best = trial

                for i in range(self.pop_size):
                    r1, r2 = np.random.choice(self.pop_size, 2, replace=False)
                    vel = self.w * population[i] + self.c1 * np.random.rand() * (population[r1] - population[i]) + self.c2 * np.random.rand() * (global_best - population[i])
                    population[i] = boundary_check(population[i] + vel)

            return global_best

        return DEPSO_optimization()
# ```
