# Description: A novel optimization algorithm combining Particle Swarm Optimization (PSO) and Simulated Annealing (SA) for black box optimization in continuous search spaces.
# Code: 
# ```python
import numpy as np

class PSO_SA_Optimizer:
    def __init__(self, budget, dim, swarm_size=30, max_iter=100):
        self.budget = budget
        self.dim = dim
        self.swarm_size = swarm_size
        self.max_iter = max_iter

    def __call__(self, func):
        def pso_sa_optimization():
            def objective_function(x):
                return func(x)

            def initialize_swarm():
                return np.random.uniform(-5.0, 5.0, size=(self.swarm_size, self.dim))

            def simulated_annealing(x, T=1.0, T_min=0.0001, alpha=0.9):
                current_cost = objective_function(x)
                while T > T_min:
                    new_x = x + np.random.normal(0, T, x.shape)
                    new_x = np.clip(new_x, -5.0, 5.0)
                    new_cost = objective_function(new_x)
                    ap = np.exp((current_cost - new_cost) / T)
                    if np.random.rand() < ap:
                        x = new_x
                        current_cost = new_cost
                    T *= alpha
                return x, current_cost

            swarm = initialize_swarm()
            swarm_best = swarm[np.argmin([objective_function(p) for p in swarm])]
            for _ in range(self.max_iter):
                for i in range(self.swarm_size):
                    new_position = swarm[i] + np.random.uniform(-1, 1, size=self.dim) * (swarm_best - swarm[i])
                    new_position = np.clip(new_position, -5.0, 5.0)
                    new_position, new_cost = simulated_annealing(new_position)
                    if new_cost < objective_function(swarm[i]):
                        swarm[i] = new_position
                        if new_cost < objective_function(swarm_best):
                            swarm_best = new_position
                if objective_function(swarm_best) < func(np.zeros(self.dim)):
                    break
            return swarm_best

        return pso_sa_optimization()
# ```
