# Description: Novel Harmony Search Algorithm for Black Box Optimization
# Code: 
# ```python
import numpy as np

class HarmonySearch:
    def __init__(self, budget, dim, hms=20, hmcr=0.9, par=0.4, bw=0.01):
        self.budget = budget
        self.dim = dim
        self.hms = hms
        self.hmcr = hmcr
        self.par = par
        self.bw = bw

    def __call__(self, func):
        def initialize_harmony_memory():
            return np.random.uniform(-5.0, 5.0, (self.hms, self.dim))

        def improvise_new_harmony(harmony_memory):
            new_harmony = np.zeros(self.dim)
            for i in range(self.dim):
                if np.random.rand() < self.hmcr:
                    new_harmony[i] = harmony_memory[np.random.choice(self.hms)][i]
                else:
                    new_harmony[i] = np.random.uniform(-5.0, 5.0)
                    if np.random.rand() < self.par:
                        new_harmony[i] += np.random.uniform(-self.bw, self.bw)
            return new_harmony

        harmony_memory = initialize_harmony_memory()
        fitness_values = np.array([func(h) for h in harmony_memory])

        for _ in range(self.budget - self.hms):
            new_harmony = improvise_new_harmony(harmony_memory)
            new_fitness = func(new_harmony)
            replace_idx = np.argmin(fitness_values)
            if new_fitness < fitness_values[replace_idx]:
                harmony_memory[replace_idx] = new_harmony
                fitness_values[replace_idx] = new_fitness

        best_idx = np.argmin(fitness_values)
        return harmony_memory[best_idx]

# ```
