# Description: Harmony Search Algorithm - A metaheuristic algorithm inspired by the improvisation process of musicians, where new solutions are generated by blending existing ones through a memory consideration and pitch adjustment strategy.
# Code:
# ```python
import numpy as np

class HarmonySearch:
    def __init__(self, budget, dim, hmcr=0.7, par=0.3, bw=0.01):
        self.budget = budget
        self.dim = dim
        self.hmcr = hmcr
        self.par = par
        self.bw = bw

    def __call__(self, func):
        def initialize_harmony_memory():
            return np.random.uniform(-5.0, 5.0, (self.budget, self.dim))

        def improvisation(harmony_memory):
            new_solution = np.copy(harmony_memory[np.random.randint(self.budget)])
            for i in range(self.dim):
                if np.random.uniform() < self.hmcr:
                    if np.random.uniform() < self.par:
                        new_solution[i] = new_solution[i] + self.bw * np.random.uniform(-1, 1)
            return new_solution

        harmony_memory = initialize_harmony_memory()
        for _ in range(self.budget):
            new_harmony = improvisation(harmony_memory)
            if func(new_harmony) < func(harmony_memory[-1]):
                harmony_memory[-1] = new_harmony
            harmony_memory = np.sort(harmony_memory, key=func)
        return harmony_memory[0]
# ```
