# Description: A hybrid metaheuristic algorithm combining Particle Swarm Optimization (PSO) with Nelder-Mead Simplex method to efficiently explore and exploit the search space within a limited budget of function evaluations.
# Code:
# ```python
import numpy as np
from scipy.optimize import minimize

class HybridPSO_NM:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim

    def __call__(self, func):
        def evaluate(x):
            return func(x)

        bounds = [(-5.0, 5.0) for _ in range(self.dim)]

        # Particle Swarm Optimization (PSO)
        def pso_optimizer():
            # PSO parameters
            c1, c2 = 2.0, 2.0
            w = 0.5
            num_particles = 10
            max_iter = int(self.budget / num_particles)

            def objective(x):
                return evaluate(x)

            # Initialize particles
            particles = np.random.uniform(-5.0, 5.0, (num_particles, self.dim))
            pbest = particles.copy()
            pbest_scores = np.array([objective(p) for p in particles])
            gbest_idx = np.argmin(pbest_scores)
            gbest = particles[gbest_idx]

            for _ in range(max_iter):
                for i in range(num_particles):
                    r1, r2 = np.random.rand(self.dim), np.random.rand(self.dim)
                    velocity = w * particles[i] + c1 * r1 * (pbest[i] - particles[i]) + c2 * r2 * (gbest - particles[i])
                    particles[i] = np.clip(particles[i] + velocity, -5.0, 5.0)
                    score = objective(particles[i])
                    if score < pbest_scores[i]:
                        pbest[i] = particles[i]
                        pbest_scores[i] = score
                        if score < pbest_scores[gbest_idx]:
                            gbest_idx = i
                            gbest = particles[i]

            return gbest

        # Nelder-Mead Simplex method
        x0 = np.random.uniform(-5.0, 5.0, self.dim)
        result = minimize(evaluate, x0, method='Nelder-Mead', bounds=bounds, options={'maxiter': self.budget})

        if result.success:
            return result.x
        else:
            return pso_optimizer()
# ```
