# Description: A novel metaheuristic algorithm inspired by the behavior of fireflies in nature for optimization across a wide range of tasks.
# Code:
# ```python
import numpy as np

class FireflyAlgorithm:
    def __init__(self, budget, dim, alpha=0.2, beta0=1.0, gamma=1.0):
        self.budget = budget
        self.dim = dim
        self.alpha = alpha
        self.beta0 = beta0
        self.gamma = gamma

    def attractiveness(self, r):
        return self.beta0 * np.exp(-self.gamma * r**2)

    def __call__(self, func):
        population = np.random.uniform(-5.0, 5.0, size=(self.budget, self.dim))
        fitness_values = np.apply_along_axis(func, 1, population)
        best_solution = population[np.argmin(fitness_values)]

        for _ in range(self.budget):
            for i in range(self.budget):
                for j in range(self.budget):
                    if fitness_values[i] < fitness_values[j]:
                        distance = np.linalg.norm(population[i] - population[j])
                        population[i] += self.alpha * (population[j] - population[i]) * self.attractiveness(distance)
                        population[i] = np.clip(population[i], -5.0, 5.0)
                        fitness_values[i] = func(population[i])
                        if fitness_values[i] < fitness_values[np.argmin(fitness_values)]:
                            best_solution = population[i]

        return best_solution
# ```
