# Description: Metaheuristic algorithm: Dynamic Simulated Annealing with Adaptive Temperature Update for Black Box Optimization
# Code: 
# ```python
import numpy as np

class DynamicSimulatedAnnealing:
    def __init__(self, budget, dim):
        self.budget = budget
        self.dim = dim
        self.lower_bound = -5.0
        self.upper_bound = 5.0

    def __call__(self, func):
        def accept_prob(delta_e, temperature):
            if delta_e < 0:
                return 1.0
            return np.exp(-delta_e / temperature)

        def update_temperature(curr_temp, iteration):
            return curr_temp * 0.95

        def generate_neighbor(solution, temperature):
            return solution + np.random.uniform(-0.1, 0.1, size=len(solution))

        current_solution = np.random.uniform(self.lower_bound, self.upper_bound, size=self.dim)
        current_cost = func(current_solution)
        temperature = 1.0

        for _ in range(self.budget):
            proposed_solution = generate_neighbor(current_solution, temperature)
            proposed_solution = np.clip(proposed_solution, self.lower_bound, self.upper_bound)
            proposed_cost = func(proposed_solution)
            cost_diff = proposed_cost - current_cost

            if accept_prob(cost_diff, temperature) > np.random.rand():
                current_solution = proposed_solution
                current_cost = proposed_cost

            temperature = update_temperature(temperature, _)

        return current_solution
