# Description: Enhanced Firefly Algorithm for Black Box Optimization, where fireflies attract each other based on their brightness and distance with a dynamic light absorption coefficient.
# Code:
# ```python
import numpy as np

class EnhancedFireflyAlgorithm:
    def __init__(self, budget, dim, alpha=1.0, beta=1.0, gamma=0.1):
        self.budget = budget
        self.dim = dim
        self.alpha = alpha
        self.beta = beta
        self.gamma = gamma

    def __call__(self, func):
        def attractiveness(r):
            return self.alpha * np.exp(-self.beta * r)

        def move_firefly(x, best, distance):
            r = np.linalg.norm(x - best)
            beta_ = self.beta * np.exp(-self.gamma * r**2)
            x_new = x + beta_ * (best - x) + distance * np.random.uniform(-1, 1, size=self.dim)
            return np.clip(x_new, -5.0, 5.0)

        def evaluate(x):
            return func(x)

        population = np.random.uniform(-5.0, 5.0, size=(self.budget, self.dim))
        best_position = population[np.argmin([evaluate(ind) for ind in population])]
        for _ in range(self.budget):
            for i, sol_i in enumerate(population):
                for j, sol_j in enumerate(population):
                    if evaluate(sol_i) < evaluate(sol_j):
                        distance = np.linalg.norm(sol_i - sol_j)
                        population[i] = move_firefly(sol_i, sol_j, distance)
            best_position = min(population, key=evaluate)
        return best_position
# ```
