# Description: A novel optimization algorithm combining Particle Swarm Optimization (PSO) with Differential Evolution (DE) to explore and exploit the search space efficiently.
# Code:
# ```python
import numpy as np

class PSO_DE_Optimizer:
    def __init__(self, budget, dim, num_particles=30, max_iter=100, w=0.5, c1=1.5, c2=1.5, f=0.5, cr=0.9):
        self.budget = budget
        self.dim = dim
        self.num_particles = num_particles
        self.max_iter = max_iter
        self.w = w
        self.c1 = c1
        self.c2 = c2
        self.f = f
        self.cr = cr

    def __call__(self, func):
        def initialize_population():
            return np.random.uniform(-5.0, 5.0, (self.num_particles, self.dim))

        def evaluate_population(population):
            return np.array([func(individual) for individual in population])

        best_solution = None
        best_fitness = np.inf
        population = initialize_population()
        fitness_values = evaluate_population(population)

        for _ in range(self.max_iter):
            for i in range(self.num_particles):
                for _ in range(self.budget // self.num_particles):
                    r1, r2, r3 = np.random.randint(0, self.num_particles, 3)
                    mutant_vector = population[r1] + self.f * (population[r2] - population[r3])

                    for j in range(self.dim):
                        if np.random.rand() < self.cr or j == np.random.randint(0, self.dim):
                            mutant_vector[j] = population[i][j]

                    trial_vector = mutant_vector + self.w * (population[i] - mutant_vector)

                    if func(trial_vector) < fitness_values[i]:
                        population[i] = trial_vector
                        fitness_values[i] = func(trial_vector)

                    if fitness_values[i] < best_fitness:
                        best_solution = population[i].copy()
                        best_fitness = fitness_values[i]

        return best_solution
# ```
